import PropTypes from 'prop-types'
import React from 'react'
import { connect } from 'react-redux'

import {
  BlurredBackground,
  InfoWrapper,
  ProfilePicture,
  TitleWrapper,
  UserAge,
  UserCardWrapper,
  UserName,
} from './styled'

import {updateCurrentPage} from '../../actions/route'

// Preciso ir para página Swipescreen
class ProfileScreen extends React.Component {
  render() {
    const { userToSwipe, animationDirection: animation } = this.props
    return (
      <div>
        <UserSwipeCard animation={animation}>
          <BlurredBackground photo={userToSwipe.photo}/>
          <ProfilePicture src={userToSwipe.photo}/>
          <InfoWrapper>
            <TitleWrapper>
              <UserName>{userToSwipe.name},</UserName>
              <UserAge>{userToSwipe.age},</UserAge>

            </TitleWrapper>
            <div>{userToSwipe.bio}</div>
          </InfoWrapper>
        </UserSwipeCard>
      </div>
    )
  }
}




ProfileScreen.propTypes = {
  goToSwipeScreen: PropTypes.func.isRequired,
  profileToSwipe: PropTypes.object,
}


const mapStateToProps = (state) => ({
  profileToSwipe: state.profiles.profileToSwipe
})

const mapDispatchToProps = (dispatch) => ({
  goToSwipeScreen: () => dispatch(updateCurrentPage('SwipeScreen')),
})

export default connect(mapStateToProps, mapDispatchToProps)(ProfileScreen)
