import axios from 'axios'

// Função síncrona
export const setProfile = (profile) => {
	return {
		type: 'SET_PROFILE',
		payload: {

			profile:profile
		}
	}
}
// Função assíncrona
const baseURL = "https://us-central1-missao-newton.cloudfunctions.net/astroMatch"


// GET Get Profile To Choose - Retorna um perfil que ainda não foi visto por você.
export const getProfile = () => async(dispatch) => {
	const response = await
		axios.get(`${ baseURL }/erika/person`)
			console.log(response.data.profile)
			dispatch(setProfile(response.data.profile)) 

}

//GET Get Matches - Retorna um array de perfis que deram match com você.
export const getMatches = () => async(dispatch) => {
	const response = await
		axios.get(`${ baseURL }/erika/matches`)
		dispatch(setProfile(response.data.matches))
		console.log(response.data.matches)
}		

//PUT Clear - Limpa todos os matches e perfis vistos.
export const clearSwipes = () => async (dispatch) => {
	await axios.put(`${ baseURL }/erika/clear`)
}




//POST Choose Person - Recebe um id e uma escolha (choice). A escolha é a opção do usuário no momento do swipe. Deve ser true ou false.


export const choosePerson = (match) => async (dispatch) => {
	const body = {
		choice:false
	}
	await
		axios.post(`${ baseURL }/erika/`, body
	);
}
